mapper.select:
select b.* from(select rownum no, a.* from( select q.q_no , q.members_no , q.q_title , q.q_contents , q.q_code , q.q_date , q.q_views , q.q_tags , m.members_id , (select count(a_no) from answer where q_no= q.q_no) a_count from members m join question q on q.members_no = m.members_no
and q_title LIKE '%' || #{searchVal} || '%' or q_contents LIKE '%' || #{searchVal} || '%'
ORDER BY ${orderby} DESC
ORDER BY q_no DESC
) a ) b where no between #{first} and #{last}

select count(*)
from rec_list
where qa_no = #{qaNo} and qa_type = #{qaType} and members_No =#{membersNo}
