mapper.update:
update wiki_guide set
			MANUAL_TITLE = #{manualTitle},
			MANUAL_ORI_URL = #{manualOriUrl},
			MANUAL_TAGS	= #{manualTags},
			MANUAL_CONTENTS_PATH = #{manualContentsPath}
where manual_no = #{manualNo}

mapper.select:
SELECT TRANS_NO as "transNo", MANUAL_LINE as "manualLine", MANUAL_BEFORE as "manualBefore", MANUAL_AFTER as "manualAfter", TO_CHAR(TRANS_DATE,'YYYY-MM-DD hh:mm:ss') as "translDate", MANUAL_NO as "manualNo", MEMBERS_NO as "membersNo"
FROM wiki_guide_trans
AND MANUAL_NO = #{manualNo}
AND MANUAL_LINE = #{manualLine}
ORDER BY ${orderby} DESC
ORDER BY MANUAL_LINE DESC
