mapper.select:
SELECT b.* FROM ( SELECT rownum no, a.* FROM ( SELECT MANUAL_NO as "manualNo", MANUAL_CONTENTS_PATH as "manualContentsPath", MANUAL_ORI_URL as "manualOriUrl", MANUAL_TAGS as "manualTags", MANUAL_TITLE as "manualTitle", MANUAL_TOTALLINE as "manualTotalline" FROM wiki_guide
AND MANUAL_TITLE LIKE '%' || #{searchVal} || '%'
AND NVL(MANUAL_TAGS, '기타,') LIKE '%' || #{searchVal} || ',%'
ORDER BY ${orderby} DESC
ORDER BY MANUAL_NO DESC
) a ) b WHERE no BETWEEN #{first} AND #{last}

mapper.update:
UPDATE wiki_guide_trans SET manual_upline = CASE
WHEN manual_line = #{index} THEN null
WHEN manual_line = #{index} THEN #{line}
END WHERE manual_no = #{manualNo}

  		UPDATE wiki_guide_trans
		SET manual_line = manual_upline
		WHERE manual_no = #{manualNo}

  	UPDATE wiki_guide_trans
	SET manual_upline = NULL
	WHERE manual_no = #{manualNo}

select.if:
SELECT TRANS_NO as "transNo", MANUAL_LINE as "manualLine", MANUAL_BEFORE as "manualBefore", MANUAL_AFTER as "manualAfter", TO_CHAR(TRANS_DATE,'YYYY-MM-DD hh:MI:ss') as "translDate", MANUAL_NO as "manualNo", MEMBERS_NO as "membersNo"
FROM wiki_guide_trans
AND MANUAL_NO = #{manualNo}
AND MANUAL_LINE = #{manualLine}
ORDER BY ${orderby} DESC
ORDER BY MANUAL_LINE DESC

mapper.select:
   		SELECT members_id
		FROM members
		WHERE members_no = #{no}

mapper.delete:
DELETE wiki_trans_rec
WHERE manual_no = #{manualNo} 
AND   trans_no =  #{transNo}
AND   members_no = #{membersNo}
